// EventPlanningPage.vue
<template>
  <div class="container my-5">
    <EventHeader :title="event.title" :location="event.location" />
    <EventDetails :description="event.description" :date="event.date" :time="event.time" />
    <DecorSection />
    <EventFooter :host="event.host" :contact="event.contact" />
  </div>
</template>

<script>
import axios from 'axios'
// import EventHeader from './EventHeader.vue'
// import EventDetails from './EventDetails.vue'
import DecorSection from './DecorSection.vue'
// import EventFooter from './EventFooter.vue'

export default {
  components: {
    EventHeader,
    EventDetails,
    DecorSection,
    EventFooter
  },
  data() {
    return {
      event: {}
    }
  },
  mounted() {
    axios.get('/api/event/1')
      .then(response => {
        this.event = response.data
      })
      .catch(error => {
        console.log(error)
      })
  }
}
</script>

<style>
/* Customize Bootstrap styles */
.event-header {
  background-color: #007bff;
  color: #fff;
  padding: 30px;
  text-align: center;
}

.event-details {
  background-color: #f8f9fa;
  border: 1px solid #ddd;
  padding: 30px;
}

.event-footer {
  background-color: #f8f9fa;
  border: 1px solid #ddd;
  padding: 30px;
}
</style>
