// EventDetails.vue
<template>
  <div class="event-details">
    <h2>Details</h2>
    <p>{{ description }}</p>
    <p>Date: {{ date }}</p>
    <p>Time: {{ time }}</p>
  </div>
</template>

<script>
export default {
  props: {
    description: String,
    date: String,
    time: String
  }
}
</script>

<style>
/* Customize Bootstrap styles */
.event-details {
  background-color: #f8f9fa;
  border: 1px solid #ddd;
  padding: 30px;
}
</style>
