import axios from 'axios';
export default {
  data() {
    return {
      eventName: '',
      eventDate: '',
      eventTime: '',
      eventLocation: ''
    };
  },
  methods: {
    submitEvent() {
      const eventData = {
        name: this.eventName,
        date: this.eventDate,
        time: this.eventTime,
        location: this.eventLocation
      };

      // Send the data to the server using axios
      axios.post('/api/events', eventData).then(response => {
        console.log(response.data);
        // Reset the form fields
        this.eventName = '';
        this.eventDate = '';
        this.eventTime = '';
        this.eventLocation = '';
      }).catch(error => {
        console.error(error);
      });
    }
  }
};
